css:
/* Overall page style */
.print-format {
    font-family: 'Arial', sans-serif;
    padding: 20px;
    color: #333;
}

/* Heading styles */
h2 {
    text-align: center;
    color: #2f4f4f;
    margin-bottom: 30px;
}

h3 {
    color: #1f4e79;
    margin-top: 30px;
    border-bottom: 2px solid #1f4e79;
    padding-bottom: 5px;
}

/* Project details section */
.project-details p {
    font-size: 14px;
    margin: 5px 0;
}

/* Task table style */
.table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;
}

.table th, .table td {
    border: 1px solid #555;
    padding: 10px;
    text-align: left;
    font-size: 13px;
}

.table th {
    background-color: #f0f0f0;
    color: #333;
}

/* Assignee name style */
.assignee-name {
    color: #007bff;
    font-weight: bold;
}

/* Footer */
.print-footer {
    text-align: center;
    font-size: 12px;
    margin-top: 50px;
    color: #777;
}
